import React from 'react';

function App() {
  const list = [{
    title: "Halloween",
    director: "John Carpenter",
    releaseYear: 1978
  },
  {
    title: "It",
    director: "Andres Muschietti",
    releaseYear: 2017
  },
  {
    title: "A Nightmare on Elm Street",
    director: "Wes Craven",
    releaseYear: 1984
  },
  {
    title: "Hereditary",
    director: "Ari Aster",
    releaseYear: 2018
  },
  {
    title: "Psycho",
    director: "Alfred Hitchcock",
    releaseYear: 1960
  }
  ]

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>
        Horror Classics:
      </h1>

      <ul>
        { list.map(function(movie) {
           return (
            <li key = {movie.ObjectID}>
              <span> <b>{movie.title}</b> </span>
              <span> directed by <b>{movie.director}</b> </span>
              <span> released in <b>{movie.releaseYear}</b> </span>
            </li>
           )
          })
        }
      </ul>
    </div>
  );
}

export default App;
